    /* needs the user to sign the transaction, so will use Web3Provider and sign it */

    setOkayMint(false);
    toast(" ⮑ Confirm Mint_Shark Transaction");
    const provider = new ethers.providers.Web3Provider(window.ethereum);

    const signer = provider.getSigner();

    const contract = new ethers.Contract(sharkTokenAddress, Token.abi, signer);

    /* user will be prompted to pay the asking proces to complete the transaction */

    let response = await contract.balanceOf(account);
    console.log(parseInt(response._hex, 16));

    let meta2 = await contract.totalSupply();
    let meta3 = parseInt(meta2._hex, 16);

    console.log(meta3);
    let url = "/shiftysharks/" + meta3;
    await contract
      .claimShark({ value: 0 })
      .then((tx) => {
        //action prior to transaction being mined

        toast("⮑ Mint_Shark Transaction Submitted", {
          duration: 7500
        });

        provider.waitForTransaction(tx.hash).then(() => {
          toast(
            <span>
              <a href={url}>⮑ Click HERE To Go To Shark {meta3}</a>
            </span>,
            {
              duration: 30000
            }
          );

          toast(<span>You have Minted Shifty Shark {meta3}! </span>, {
            duration: 10000
          });

          setOkayMint(true);
        });
      })
      .catch(() => {
        //action to perform when user clicks "reject"
        toast("✘ Mint_Shark Transaction Rejected");
        setOkayMint(true);
      });
